import { useParams } from "react-router";
import Footer from "../Navbar/Footer";
import Navbar from "../Navbar/navbar";
import { useEffect, useState } from "react";
import axios from "axios";
import { fetchWithToken } from "../API/interceptor";
import "../TheaterPage/theaterseat.css"






function TheaterSeats() {

    const params = useParams()
    const [movies, setMovies] = useState()
    const [theaters, setTheater] = useState()

    const [seats, setSeats] = useState();
    let movie_id = params.movie_id;
    let theater_id = params.theater_id;
    useEffect(() => {
        async function fetchSeats() {
            try {
                const response = await fetchWithToken("http://127.0.0.1:8000/api/movies/theaters/seats/?movie=" + movie_id + "&theater=" + theater_id);
                if (response.ok) {
                    const result = await response.json()
                    setSeats(result)
                }
            }
            catch (error) {
                console.log(error)
            }

        }
        fetchSeats()
    }, [])




    useEffect(() => {
        async function fetchMovie() {
            try {
                const response = await fetchWithToken("http://127.0.0.1:8000/api/movies/single/?id=" + movie_id);
                if (response.ok) {
                    const result = await response.json()
                    setMovies(result)
                }
            }
            catch (error) {
                console.log(error)
            }

        }
        fetchMovie()
    }, [])
    useEffect(() => {
        async function fetchTheater() {
            try {
                const response = await fetchWithToken("http://127.0.0.1:8000/api/theater/?theater=" + theater_id);
                if (response.ok) {
                    const result = await response.json()
                    setTheater(result)
                }
            }
            catch (error) {
                console.log(error)
            }

        }
        fetchTheater()
    }, [])
    // console.log(theaters)

    let theater;
    if (theaters) {
        theater = theaters["theaters"];
    }

    let movie;
    if (movies) {
        movie = movies["movies"];
    }

    // console.log(seats)
    function checkAvailability(seatno) {

        let isavailable=false;
        if (seats) {
            for (let i = 0; i < seats.length; i++) {


                if (seatno == seats[i].number) {
                    isavailable=true
                    break;
                }


            }

        }
        return isavailable

    }
// console.log(checkAvailability(10))
    return (
        <>
            <Navbar></Navbar>
            <div className="container" style={{ justifyContent: "center",alignItems:"center" }}>
                <div className="col-md-6" style={{ height: "100vh", alignItems: "center", justifyContent: "center", width: "auto" }}>
                    {movie && movie.map((p, i) => (
                        <div className="movidetails">
                            <p style={{ color: "white", fontSize: "1.5em", textShadow: "2px 2px 2px black" }}>Movie: {p.title}</p>
                        </div>
                    ))

                    }
                    {theater && theater.map((p, i) => (
                        <div className="theaterdetails">
                            <p style={{ color: "white", fontSize: "1.5em", textShadow: "2px 2px 2px black" }}>Theater: {p.name}</p>
                            <p style={{ color: "white", fontSize: "1em", textShadow: "2px 2px 2px black" }}>Show Time: {p.timing}</p>
                        </div>

                    ))

                    }

                    <div className="screendiv">
                        <img src="screen.png" alt="Load Image" />
                        <div className="seatsec" style={{display:"flex",flexDirection:"column" ,gap:"5px",justifyContent:"center",alignItems:"center"}}>
                            <span style={{display:"flex",gap:"5px"}}>
                                <button className="seatbutton" id="1" disabled={checkAvailability(1)}>A1</button>
                                <button className="seatbutton" id="2" disabled={checkAvailability(2)}>A2</button>
                                <button className="seatbutton" id="3" disabled={checkAvailability(3)}>A3</button>
                                <button className="seatbutton" id="4" disabled={checkAvailability(4)}>A4</button>
                                <button className="seatbutton" id="5" disabled={checkAvailability(5)}>A5</button>
                                <button className="seatbutton" id="6" disabled={checkAvailability(6)}>A6</button>
                                <button className="seatbutton" id="7" disabled={checkAvailability(7)}>A7</button>
                                <button className="seatbutton" id="8" disabled={checkAvailability(8)}>A8</button>
                                <button className="seatbutton" id="9" disabled={checkAvailability(9)}>A9</button>
                                <button className="seatbutton" id="10" disabled={checkAvailability(10)}>A10</button>

                            </span>
                            <span>
                                <button className="seatbutton" id="11" disabled={checkAvailability(11)}>B1</button>
                                <button className="seatbutton" id="12" disabled={checkAvailability(12)}>B2</button>
                                <button className="seatbutton" id="13" disabled={checkAvailability(13)}>B3</button>
                                <button className="seatbutton" id="14" disabled={checkAvailability(14)}>B4</button>
                                <button className="seatbutton" id="15" disabled={checkAvailability(15)}>B5</button>
                                <button className="seatbutton" id="16" disabled={checkAvailability(16)}>B6</button>
                                <button className="seatbutton" id="17" disabled={checkAvailability(17)}>B7</button>
                                <button className="seatbutton" id="18" disabled={checkAvailability(18)}>B8</button>
                                <button className="seatbutton" id="19" disabled={checkAvailability(19)}>B9</button>
                                <button className="seatbutton" id="20" disabled={checkAvailability(20)}>B10</button>

                            </span>

                        </div>
                    </div>
                </div>
                {/* <div className="col-md-3"></div> */}
            </div>
            <Footer></Footer>
        </>
    )

}



export default TheaterSeats;
