import "../Moviepage/moviepage.css"
import { useEffect, useState } from "react";
import axios from "axios";
import Loading from "../Home/Loading";


function Movie() {

    const [movies, setMovies] = useState()

    useEffect(() => {
        async function getMovies() {
            await axios.get("http://127.0.0.1:8000/api/movies/?page=1")
                .then((response) => {

                    setMovies(response.data)

                },
                    (error) => {
                        console.log(error);
                    })
                .catch((error) => {
                    console.log(error)
                })
        }

        getMovies();
    }, [])
    // console.log(movies.total_pages)
    function isPage(page_number) {
        if (movies) {
            if (page_number <= movies.total_pages) {
                return true
            }
            else { 
                return false 
            }
        }

    }

    function click() {
        console.log("hello")
    }
    return (
        <>
            {movies &&
                movies["movies"].map((i, p) => (
                    <div className="col-md-3" onClick={click}>
                        <div className="card-main" id={i.movie_id}>
                            <img src={i.image} id={i.movie_id} className="card-img-top" alt="LoadImage"></img>
                            <h5 className="product-title" id={i.movie_id} >{i.title}</h5>
                            <p className="card-text" id={i.movie_id}>Director: {i.director}</p>
                            <p className="card-text genre" id={i.movie_id}>Genre: {i.genre}</p>
                            <p className="card-text genre" id={i.movie_id}>Duration: {i.duration} Mins</p>
                            <button type="button" className="btn btn-primary btn-lg">Book Now</button>
                        </div>
                    </div>)
                )
            }
            {!movies &&
                (<div className="loading"><Loading></Loading></div>)}


            <div className="pagination">
                <button className="btn btn-primary btn-lg page"disabled={!isPage(1) } onClick={click}>1</button>
                <button className="btn btn-primary btn-lg page" disabled={!isPage(2)} onClick={click}>2</button>
                <button className="btn btn-primary btn-lg page" disabled={!isPage(3)} onClick={click}>3</button>
                <button className="btn btn-primary btn-lg page" disabled={!isPage(4)} onClick={click}>4</button>
            </div>


        </>




    )
}



export default Movie;
