import { useState } from "react";
import Navbar from "../Navbar/navbar";
import * as Yup from "yup";
import axios from 'axios';

import { Formik, Field, Form, ErrorMessage, useFormik } from "formik";
import { useNavigate } from "react-router";
import "../Register/login.css";
function Login() {
    const navigate = useNavigate();

    const initialvalues = {
        username: "",
        password: ""
    }

    const validationschema = Yup.object({
        username: Yup.string().required("Username is Required!!"),
        password: Yup.string().required("Password is Required!!")
    })


    const [responseData, setResponseData] = useState({
        responseText: "",
        responseClass: ""
    })

    async function onSubmit(values, { resetForm }) {
        await axios.post("http://127.0.0.1:8000/api/users/login", values)
            .then((response) => {
                localStorage.setItem("access_token", response.data.access_token)
                localStorage.setItem("refresh_token", response.data.refresh_token)

                setResponseData({
                    responseText: "Login Successfull",
                    responseClass: "alert alert-success"
                });
                setInterval(() => {
                    navigate("/", true)
                }, 1000)
            },
                (error) => {
                    setResponseData({
                        responseText: "Invalid username or password ",
                        responseClass: "alert alert-danger"
                    })
                }
            )
            .catch((error) => {
                console.log(error);

            })
        resetForm();

        setTimeout(()=>{
            window.location.reload();

        },2000)



    }





    return (
        <>
            <Navbar></Navbar>

            <div className="container maincontainer">
                <div className="row">
                    <div className="col-md-3"></div>
                    <div className="col-md-6">
                        <div className="mainform">
                            <h1 className="logg">Login</h1>
                            <div className={responseData.responseClass} role="alert">{responseData.responseText}</div>
                            <br></br>
                            <Formik initialValues={initialvalues} onSubmit={onSubmit} validateOnMount validationSchema={validationschema}>
                                {(formik)=>{
                                    return(
                                        <Form>
                                            <div className="form-group">
                                                <label htmlFor="username">Username</label>
                                                <Field type="text" name="username" className="form-control"/>
                                                <ErrorMessage name="username">{(errormessage)=>(<small className="text-danger">{errormessage}</small>)}</ErrorMessage>
                                            </div>

                                            <div className="form-group">
                                                <label htmlFor="password">Password</label>
                                                <Field type="password" name="password" className="form-control"/>
                                                <ErrorMessage name="password">{(errormessage)=>(<small className="text-danger">{errormessage}</small>)}</ErrorMessage>
                                            </div>
                                            <button type="submit" class="btn btn-primary" value="login" disabled={!formik.isValid}>Submit</button>
                                        </Form>
                                    )
                                }}
                            </Formik>
                        </div>
                    </div>
                    <div className="col-md-3"></div>
                </div>
            </div>
            {/* </div> */}

        </>
    )

}


export default Login;
