import "../Moviepage/moviepage.css"
import { useEffect, useState } from "react";
import axios from "axios";
import Loading from "../Home/Loading";


function Movie() {

    const [movies, setMovies] = useState()

    useEffect(() => {
        async function getMovies() {
            await axios.get("http://127.0.0.1:8000/api/movies/?page=1")
                .then((response) => {

                    setMovies(response.data)

                },
                    (error) => {
                        console.log(error);
                    })
                .catch((error) => {
                    console.log(error)
                })
        }

        getMovies();
    }, [])

    function isPage(page_number) {
        if (movies) {
            if (page_number <= movies.total_pages) {
                return true
            }
            else {
                return false
            }
        }

    }

    async function click(e) {


        await axios.get("http://127.0.0.1:8000/api/movies/?page=" + e.target.id)
            .then((response) => {

                setMovies(response.data)

            },
                (error) => {
                    console.log(error);
                })
            .catch((error) => {
                console.log(error)
            })
    }

    let buttons = []
    if (movies) {
        for (let i = 1; i <= movies.total_pages + 2; i++) {
            buttons.push(<button className="btn btn-primary btn-lg page" disabled={!isPage(i)} id={i} onClick={click}>{i}</button>)
        }
    }





    return (
        <>
            <div className="filter-section">
                {/* <nav className="navbar navbar-expand-lg navbar-light bg-light" style={{ "display": "flex", "justifyContent": "space-around" }}>
                    <select className="form-select" aria-label="Default select example" style={{ "width": "15vw", "margin": "5px" }}>
                        <option selected>Genre</option>
                        <option value="1">Action</option>
                        <option value="2">Comedy</option>
                        <option value="3">Drama</option>
                        {/* <option value="3">Drama</option> */}
