import { useParams } from "react-router";
import Footer from "../Navbar/Footer";
import Navbar from "../Navbar/navbar";
import { useState } from "react";
import { fetchWithToken } from "../API/interceptor";




function Bookingpage(){

    const params=useParams()
    let movie_id = params.movie_id;
    let theater_id = params.theater_id;
    const [userbooking,setUserBooking]=useState()

    useEffect(() => {
        async function fetchTheater() {
            try {
                const response = await fetchWithToken("http://127.0.0.1:8000/api/user/booking/?movie="+movie_id+"&theater="+theater_id);
                if (response.ok) {
                    const result = await response.json()
                    setUserBooking(result)
                }
            }
            catch (error) {
                console.log(error)
            }

        }
        fetchTheater()
    }, [])


    return(<>
        <Navbar></Navbar>
        <div className="bookingPart" style={{height:"100vh"}}>


        </div>
        <Footer></Footer>
    </>)
}


export default Bookingpage;
