import { useParams } from "react-router";
import Footer from "../Navbar/Footer";
import Navbar from "../Navbar/navbar";
import { useEffect, useState } from "react";
import { fetchWithToken } from "../API/interceptor";





function UserBookings() {


    const params = useParams()


    const [userBookings, setUserBookings] = useState([]);
    const [movies, setMovies] = useState()

    useEffect(() => {
        async function fetchUserBookings() {
            try {
                const response = await fetchWithToken("http://127.0.0.1:8000/api/user/bookings/?user=" + params['user']);
                if (response.ok) {
                    const data = await response.json();
                    setUserBookings(data);
                }
            } catch (error) {
                console.log(error);
            }
        }

        async function fetchAllMovies() {
            try {
                const response = await fetchWithToken("http://127.0.0.1:8000/api/movies/");
                if (response.ok) {
                    const data = await response.json();
                    // console.log("Fetched movies data:", data);
                    setMovies(data.movies); // Use data.movies to set movies
                }
            } catch (error) {
                console.log(error);
            }
        }

        fetchUserBookings();
        fetchAllMovies();
    }, []);



    console.log("Movies:", movies); 

    console.log(movies)

    // const getMovieName = (movieId) => {
    //     const foundMovie = movies.find((movie) => movie.id === movieId);
    //     return foundMovie ? foundMovie.title : "Unknown Movie";
    // };



    console.log(userBookings)
    return (
        <>
            <Navbar></Navbar>
            <div className="container" style={{ justifyContent: "center", alignItems: "center", color: "white", textShadow: "2px 2px 3px black", gap: "20px" }}>

                <h2>My Bookings</h2>
                <hr />
                {userBookings.map((booking) => (
                    <div style={{ display: "flex", boxShadow: "2px 2px 5px black", width: "30vw", height: "20vh", alignItems: "center", justifyContent: "center" }}>
                        <h2 key={booking.booking_id}>
                            Movie: <br />
                            Theater: {booking.theater}<br />
                            Seats :{booking.seat_numbers}
                            {/* Add more details as needed */}
                        </h2>
                    </div>
                ))}


            </div>
            <Footer></Footer>
        </>
    )
}


export default UserBookings;
