import { useState } from "react";
import Navbar from "../Navbar/navbar";
import * as Yup from "yup";
import axios from 'axios';

import { Formik, Field, Form, ErrorMessage, useFormik } from "formik";
import { useNavigate } from "react-router";

function Login() {
    const navigate=useNavigate();

    const initialvalues={
        username:"",
        password:""
    }

    const validationschema= Yup.object({
        username:Yup.string().required("Username is Required!!"),
        password:Yup.string().required("Password is Required!!")
    })


    const [responseData, setResponseData] = useState({
        responseText: "",
        responseClass: ""
    })

    async function onSubmit(values,{restForm}){
        await axios.post("http://127.0.0.1:8000/api/users/login",values)
        .then((response)=>{
            localStorage.setItem("access_token",response.data.access_token)
            localStorage.setItem("refresh_token",response.data.refresh_token)

            setResponseData({
                responseText:"Login Successfull",
                responseClass:"alert alert-success"
            });
            setInterval(()=>{
                navigate("/",true)
            },1000)
        },
        (error)=>{
            setResponseData({
                responseText:"Invalid username or password!!",
                responseClass:"alert alert-danget"
            })
        }
        )
        .catch((error)=>{
            console.log(error);

        })
        restForm();


    }





    return (
        <>
            <Navbar></Navbar>
            <div className="container">
                <div className="row">
                    <div className="col-md-3"></div>
                    <div className="col-md-6">

                    </div>
                </div>
            </div>

        </>
    )

}


export default Login;
