import "../Moviepage/moviepage.css"
import { useEffect, useState } from "react";
import axios from "axios";
import Loading from "../Home/Loading";


function Movie() {

    const [movies, setMovies] = useState()

    useEffect(() => {
        async function getMovies() {
            await axios.get("http://127.0.0.1:8000/api/movies/?page=1")
                .then((response) => {

                    setMovies(response.data)

                },
                    (error) => {
                        console.log(error);
                    })
                .catch((error) => {
                    console.log(error)
                })
        }

        getMovies();
    }, [])

    console.log(movies)
    return (
        <>
            {movies &&
                movies["movies"].map((i, p) => (<div className="card-main" id={movies.movie_id}>
                    <img src={movies.image} id={movies.movie_id} className="card-img-top" alt="LoadImage"></img>
                    <h5 className="product-title" id={movies.movie_id} >{movies.title}</h5>
                </div>))
            }
        </>




    )
}



export default Movie;
