import { useParams } from "react-router";
// import { useHistory } from 'react-router-dom';
import Footer from "../Navbar/Footer";
import Navbar from "../Navbar/navbar";
import { useEffect, useState } from "react";
import { fetchWithToken } from "../API/interceptor";




function Bookingpage() {


    const params = useParams();
    const movie_id = params.movie_id;
    const theater_id = params.theater_id;
    const [userbooking, setUserBooking] = useState();

    useEffect(() => {
        async function fetchBooking() {
            try {
                const response = await fetchWithToken("http://127.0.0.1:8000/api/user/booking/?movie=" + movie_id + "&theater=" + theater_id);
                if (response.ok) {
                    const result = await response.json();
                    setUserBooking(result);
                }
            } catch (error) {
                console.log(error);
            }
        }
        fetchBooking();
    }, []);
    console.log(userbooking)

    const [movie, setMovie] = useState()
    const [theater, setTheater] = useState()


    useEffect(() => {
        async function fetchMovie() {
            try {
                const response = await fetchWithToken("http://127.0.0.1:8000/api/booking/movie/?movie=" + movie_id);
                if (response.ok) {
                    const result = await response.json();
                    setMovie(result);
                }
            } catch (error) {
                console.log(error);
            }
        }
        fetchMovie();
    }, []);


    useEffect(() => {
        async function fetchTheater() {
            try {
                const response = await fetchWithToken("http://127.0.0.1:8000/api/theater/?theater=" + theater_id);
                if (response.ok) {
                    const result = await response.json();
                    setTheater(result);
                }
            } catch (error) {
                console.log(error);
            }
        }
        fetchTheater();
    }, []);

    let m=movie["movies"]
    console.log(m)
    return (<>
        <Navbar></Navbar>


        <div className="bookingPart" style={{ height: "100vh", width: "80vh", display: "flex", flexDirection: "column", justifyContent: "center", alignItems: "center" }}>
            <div className="moviepart">

                {movie &&
                    (
                        <h5>{movie["movies"].title}</h5>

                    )}

            </div>


        </div>
        <Footer></Footer>
    </>)
}


export default Bookingpage;
