                                <div className="seat-row">
                                    <div id="41" className={checkAvailability(41) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(41, 400)} >F1</div>
                                    <div id="42" className={checkAvailability(42) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(42, 400)} >F2</div>
