import { useParams } from "react-router";
import Footer from "../Navbar/Footer";
import Navbar from "../Navbar/navbar";
import { useEffect, useState } from "react";
import axios from "axios";
import { fetchWithToken } from "../API/interceptor";
import "../TheaterPage/theaterseat.css"
import screen from "./screen.png"





function TheaterSeats() {

    const params = useParams()
    const [movies, setMovies] = useState()
    const [theaters, setTheater] = useState()

    const [seats, setSeats] = useState();
    const [selectedseat,setSelectedSeat] = useState([]);
    const [amount ,setAmount]=useState([])
    let movie_id = params.movie_id;
    let theater_id = params.theater_id;
    useEffect(() => {
        async function fetchSeats() {
            try {
                const response = await fetchWithToken("http://127.0.0.1:8000/api/movies/theaters/seats/?movie=" + movie_id + "&theater=" + theater_id);
                if (response.ok) {
                    const result = await response.json()
                    setSeats(result)
                }
            }
            catch (error) {
                console.log(error)
            }

        }
        fetchSeats()
    }, [])




    useEffect(() => {
        async function fetchMovie() {
            try {
                const response = await fetchWithToken("http://127.0.0.1:8000/api/movies/single/?id=" + movie_id);
                if (response.ok) {
                    const result = await response.json()
                    setMovies(result)
                }
            }
            catch (error) {
                console.log(error)
            }

        }
        fetchMovie()
    }, [])
    useEffect(() => {
        async function fetchTheater() {
            try {
                const response = await fetchWithToken("http://127.0.0.1:8000/api/theater/?theater=" + theater_id);
                if (response.ok) {
                    const result = await response.json()
                    setTheater(result)
                }
            }
            catch (error) {
                console.log(error)
            }

        }
        fetchTheater()
    }, [])
    // console.log(theaters)

    let theater;
    if (theaters) {
        theater = theaters["theaters"];
    }

    let movie;
    if (movies) {
        movie = movies["movies"];
    }

    // console.log(seats)
    function checkAvailability(seatno) {

        let isavailable = false;
        if (seats) {
            for (let i = 0; i < seats.length; i++) {


                if (seatno == seats[i].number) {
                    isavailable = true
                    break;
                }


            }

        }
        return isavailable

    }





    let total = 0;



    function seatSelect(number, value) {

        const index1 = selectedseat.indexOf(number);
        const index2 = amount.indexOf(value);

        if (index1 !== -1 && index2 !== -1) {
            selectedseat.splice(index1, 1);
            amount.splice(index2, 1);
        } else if (selectedseat.length < 6) {
            // seat.push(number);
            setSelectedSeat([...selectedseat,number])

            // amount.push(value);
            setAmount([...amount,value])
        } else {
            alert("Cannot select more than 6 seats!!");
            return;
        }

        let element = document.getElementById(number);
        if (element) {
            element.classList.toggle("selected");
        }



        console.log(selectedseat);
        console.log(amount);




    }









    // function myFunc(total,num){
    //     return total+num;
    // }



    return (
        <>
            <Navbar></Navbar>
            <div className="container" style={{ justifyContent: "center", alignItems: "center" }}>
                <div className="col-md-12" style={{ height: "100vh", alignItems: "center", justifyContent: "center", width: "auto" }}>
                    {movie && movie.map((p, i) => (
                        <div className="movidetails">
                            <p style={{ color: "white", fontSize: "1.5em", textShadow: "2px 2px 2px black" }}>Movie: {p.title}</p>
                        </div>
                    ))

                    }
                    {theater && theater.map((p, i) => (
                        <div className="theaterdetails">
                            <p style={{ color: "white", fontSize: "1.5em", textShadow: "2px 2px 2px black" }}>Theater: {p.name}</p>
                            <p style={{ color: "white", fontSize: "1em", textShadow: "2px 2px 2px black" }}>Show Time: {p.timing}</p>
                        </div>

                    ))

                    }

                    <div class="movie-container">



                        <div className="container" >

                            <ul class="showcase" style={{ color: "white" }} >
                                <li>
                                    <div class="seat selected"></div>
                                    <small>Selected</small>
                                </li>
                                <li>
                                    <div class="seat occupied"></div>
                                    <small>Occupied</small>
                                </li>
                            </ul>
                            <p style={{ color: "white", fontSize: "2em", textShadow: "2px 2px 5px black" }}>Screen This Side</p>
                            <div className="screen"></div>

                            <div className="seat-row">


                                <div id="1" className={checkAvailability(1) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(A1, 150)}>A1</div>
                                <div id="2" className={checkAvailability(2) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(A2, 150)} >A2</div>
                                <div id="3" className={checkAvailability(3) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(A3, 150)}  >A3</div>
                                <div id="4" className={checkAvailability(4) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(A4, 150)}  >A4</div>
                                <div id="5" className={checkAvailability(5) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(A5, 150)}  >A5</div>
                                <div id="6" className={checkAvailability(6) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(A6, 150)}  >A6</div>
                                <div id="7" className={checkAvailability(7) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(A7, 150)}  >A7</div>
                                <div id="8" className={checkAvailability(8) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(A8, 150)}  >A8</div>
                            </div>
                            <div className="seat-row">
                                <div id="9" className={checkAvailability(9) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(9, 200)} >B1</div>
                                <div id="10" className={checkAvailability(10) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(10, 200)} >B2</div>
                                <div id="11" className={checkAvailability(11) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(11, 200)} >B3</div>
                                <div id="12" className={checkAvailability(12) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(12, 200)} >B4</div>
                                <div id="13" className={checkAvailability(13) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(13, 200)} >B5</div>
                                <div id="14" className={checkAvailability(14) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(14, 200)} >B6</div>
                                <div id="15" className={checkAvailability(15) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(15, 200)} >B7</div>
                                <div id="16" className={checkAvailability(16) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(16, 200)} >B8</div>
                            </div>
                            <div class="seat-row">
                                <div id="17" className={checkAvailability(17) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(17, 250)} >C1</div>
                                <div id="18" className={checkAvailability(18) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(18, 250)} >C2</div>
                                <div id="19" className={checkAvailability(19) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(19, 250)} >C3</div>
                                <div id="20" className={checkAvailability(20) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(20, 250)} >C4</div>
                                <div id="21" className={checkAvailability(21) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(21, 250)} >C5</div>
                                <div id="22" className={checkAvailability(22) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(22, 250)} >C6</div>
                                <div id="23" className={checkAvailability(23) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(23, 250)} >C7</div>
                                <div id="24" className={checkAvailability(24) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(24, 250)} >C8</div>
                            </div>
                            <div class="seat-row">
                                <div id="25" className={checkAvailability(25) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(25, 300)} >D1</div>
                                <div id="26" className={checkAvailability(26) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(26, 300)} >D2</div>
                                <div id="27" className={checkAvailability(27) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(27, 300)} >D3</div>
                                <div id="28" className={checkAvailability(28) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(28, 300)} >D4</div>
                                <div id="29" className={checkAvailability(29) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(29, 300)} >D5</div>
                                <div id="30" className={checkAvailability(30) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(30, 300)} >D6</div>
                                <div id="31" className={checkAvailability(31) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(31, 300)} >D7</div>
                                <div id="32" className={checkAvailability(32) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(32, 300)} >D8</div>
                            </div>
                            <div className="seat-row">
                                <div id="33" className={checkAvailability(33) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(33, 350)} >E1</div>
                                <div id="34" className={checkAvailability(34) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(34, 350)} >E2</div>
                                <div id="35" className={checkAvailability(35) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(35, 350)} >E3</div>
                                <div id="36" className={checkAvailability(36) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(36, 350)} >E4</div>
                                <div id="37" className={checkAvailability(37) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(37, 350)} >E5</div>
                                <div id="38" className={checkAvailability(38) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(38, 350)} >E6</div>
                                <div id="39" className={checkAvailability(39) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(39, 350)} >E7</div>
                                <div id="40" className={checkAvailability(40) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(40, 350)} >E8</div>
                            </div>
                            <div className="seat-row">
                                <div id="41" className={checkAvailability(41) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(41, 400)} >F1</div>
                                <div id="42" className={checkAvailability(42) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(42, 400)} >F2</div>
                                <div id="43" className={checkAvailability(43) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(43, 400)} >F3</div>
                                <div id="44" className={checkAvailability(44) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(44, 400)} >F4</div>
                                <div id="45" className={checkAvailability(45) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(45, 400)} >F5</div>
                                <div id="46" className={checkAvailability(46) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(46, 400)} >F6</div>
                                <div id="47" className={checkAvailability(47) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(47, 400)} >F7</div>
                                <div id="48" className={checkAvailability(48) == false ? "seat" : "seat occupied"} onClick={() => seatSelect(48, 400)} >F8</div>
                            </div>

                            <p className="text" style={{ color: "white" }}>
                                You have selected <span id="count">{selectedseat.join(",")}</span> seats for the total price of Rs. <span id="total">{amount.reduce((prev, item) => prev + item, 0)} </span>
                            </p>
                            <button className="btn btn-primary">Book Now</button>
                            <br></br>
                        </div>
                    </div>


                </div>

                {/* <div className="col-md-3"></div> */}
            </div>
            <Footer></Footer>
        </>
    )

}



export default TheaterSeats;
