import { useParams } from "react-router";
import Navbar from "../Navbar/navbar";
import { useEffect, useState } from "react";
import axios from "axios";
import Loading from "../Home/Loading";
import "../Moviepage/moviepage.css"



function SingleMovie() {

    const [movies, setMovies] = useState()
    let params = useParams()
    let id = params.id
    // console.log(id)
    useEffect(() => {
        axios.get("http://127.0.0.1:8000/api/movies/single/?id=" + id)
            .then((response) => {
                // console.log(response.data)
                setMovies(response.data)

            }, (error) => {
                console.log(error)
            })
            .catch((error) => {
                console.log(error)
            })
    }, [])
    let movie
    if (movies) {
        movie = movies["movies"]
    }
    const [theaters, setTheaters] = useState()

    useEffect(() => {
        axios.get("http://127.0.0.1:8000/api/movies/theaters/?movie=" + id + "&page=1")
            .then((response) => {
                setTheaters(response.data)
            })

    }, [])
    console.log(theaters)

    let buttons = []
    if (theaters) {
        for (let i = 1; i <= theaters.total_pages + 2; i++) {
            buttons.push(<button className="btn btn-primary btn-lg page" disabled={!isPage(i)} id={i} onClick={click}>{i}</button>)
        }
    }

    return (
        <>
            <Navbar></Navbar>
            <div className="thtrdiv">
                <div className="col-md-3" style={{ margin: "1em", display: "flex" }}>
                    {movie &&
                        movie.map((i, p) => (
                            <div class="card" style={{ "width": "18rem", "backgroundColor": "transparent" }}>
                                <img src={i.image} class="card-img-top" alt="..." />
                                <div class="card-body">
                                    <h5 className="product-title" id={i.movie_id} >{i.title}</h5>
                                    <p className="card-text" id={i.movie_id}>Director: {i.director}</p>
                                    <p className="card-text" id={i.movie_id}>Language: {i.language}</p>
                                    <p className="card-text genre" id={i.movie_id}>Genre: {i.genre}</p>
                                    <p className="card-text genre" id={i.movie_id}>Duration: {i.duration} Mins</p>
                                    {/* <a href="#" class="btn btn-primary">Go somewhere</a> */}
                                </div>

                            </div>
                        ))

                    }
                </div>

                <div className="col-md-6" style={{ margin: "1em", display: "flex", flexDirection: "column" }}>
                    {theaters &&
                        theaters["theaters"].map((i, p) => (
                            <div class="card-body">
                                <h5 className="product-title" id={i.movie_id} >{i.name}</h5>
                                <p className="card-text" id={i.movie_id}>Address: {i.address}</p>
                                <p className="card-text" id={i.movie_id}>Pincode: {i.pincode}</p>
                                <p className="card-text genre" id={i.movie_id}>Timings: {i.timing}</p>

                                <a href="#" class="btn btn-primary">Book Now</a>
                            </div>

                        ))}
                    {! theaters && 

                        (<div className="loading"><Loading></Loading></div>)

                    }
                </div>
            </div>
            <div className="pagination">
                {buttons}
            </div>
        </>
    )

}


export default SingleMovie;
